import { Component } from '@angular/core';
import { AuthService } from '../services/AuthService';
import { UserService } from '../services/UserService';
import { CommonFunctionalityService } from '../services/CommonFunctionalityService';
import { Router } from '@angular/router';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  isExpanded = false;
  isLoggedIn = false;

constructor(private authService: AuthService, private userService: UserService, private router: Router, private common: CommonFunctionalityService) {

    this.authService.isLoggedIn.subscribe(value => {
      this.isLoggedIn = value;
    });

  }

  logout() {
    this.userService.logout();
    this.router.navigate(['/home']);
    //this.common.reloadPage();
  }

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }
}
