import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { CustomerInterface } from '../../interfaces/CustomerInterface';

@Component({
  selector: 'login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  title = 'CUSTOMER LOGIN';

  accountForm: FormGroup;

  constructor(private router: Router, private http: HttpClient) {
    this.accountForm = new FormGroup({
      UserName: new FormControl('', [Validators.required, Validators.minLength(4)]),
      Password: new FormControl('', [Validators.required, Validators.pattern(/(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*#?&^_-]).{8,}/)])
    });
  }

  ngOnInit(): void {

  }

  public validateControl = (controlName: string) => {
    return this.accountForm.get(controlName)!.invalid && this.accountForm.get(controlName)!.touched;
  }

  public loginUser = (loginFormValue: any) => {
    const formValues = { ...loginFormValue };
    const customer: CustomerInterface = {
      UserName: formValues.UserName,
      Password: formValues.Password
    };
  };
}
<section class="vh-90 gradient-custom">
  <div class="container py-2 h-90">
    <div class="row justify-content-center align-items-center h-100">
      <div class="col-12 col-lg-9 col-xl-7">
        <div class="card shadow-2-strong card-login forms" style="border-radius: 20px;">
          <div class="card-body p-3 p-md-4">
            <h3 class="mb-4 pb-2 pb-md-0 mb-md-5">Login Form</h3>
            <form [formGroup]="accountForm" autocomplete="on" novalidate (ngSubmit)="registerUser(accountForm.value)" enctype="multipart/form-data">
              <div class="row">
                <div class="col-md-12 mb-4 pb-2">
                  <div class="form-outline">
                    <input type="text" id="UserName" formControlName="UserName" class="form-control form-control-lg" />
                    <label class="form-label" for="UserName">Username</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-6 mb-4 pb-2">
                  <div class="form-outline">
                    <input type="password" id="Password" formControlName="Password" class="form-control form-control-lg" />
                    <label class="form-label" for="Password">Password</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-12 mb-4">
                  <button type="submit" class="btn btn-primary btn-lg">PROCEED</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
