import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
}
<!DOCTYPE html>
<html>
<head>
  <title>Home Page</title>
  <style>
    body {
      background: linear-gradient(to bottom, #000080, #0000FF);
    }

    .container {
      max-width: 600px;
      margin: 0 auto;
      padding: 40px;
      text-align: center;
      background-color: white;
      border-radius: 5px;
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
    }

    h1 {
      font-family: 'Arial', sans-serif;
      color: #333;
      font-size: 36px;
    }

    p {
      font-family: 'Verdana', sans-serif;
      color: #777;
      font-size: 18px;
    }
  </style>
</head>
<body>
  <div class="container">
    <h1>Welcome to the Home Page</h1>
    <p>Please Register yourself first before Logging in to further utilize the program.</p>
  </div>
</body>
</html>
