import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { ItemInterface } from '../../interfaces/ItemInterface';

@Component({
  selector: 'additem',
  templateUrl: './additem.component.html',
  styleUrls: ['./additem.component.css']
})
export class AddItemComponent implements OnInit {

  title = 'Add Item Page';

  accountForm: FormGroup;

  constructor(private router: Router, private http: HttpClient) {
    this.accountForm = new FormGroup({
      ItemCode: new FormControl('', [Validators.required, Validators.minLength(4)]),
      ItemName: new FormControl('', [Validators.required, Validators.minLength(3)]),
      ItemBrand: new FormControl('', [Validators.required, Validators.minLength(3)]),
      UnitPrice: new FormControl('', [Validators.required, Validators.minLength(3)])
    });
  }

  ngOnInit(): void {

  }

  public validateControl = (controlName: string) => {
    return this.accountForm.get(controlName)!.invalid && this.accountForm.get(controlName)!.touched;
  }

  public additemUser = (additemFormValue: any) => {
    const formValues = { ...additemFormValue };
    const item: ItemInterface = {
      ItemCode: formValues.ItemCode,
      ItemName: formValues.ItemName,
      ItemBrand: formValues.ItemBrand,
      UnitPrice: formValues.UnitPrice
    };
  };
}
<section class="vh-90 gradient-custom">
  <div class="container py-2 h-90">
    <div class="row justify-content-center align-items-center h-100">
      <div class="col-12 col-lg-9 col-xl-7">
        <div class="card shadow-2-strong card-additem forms" style="border-radius: 20px;">
          <div class="card-body p-3 p-md-4">
            <h3 class="mb-4 pb-2 pb-md-0 mb-md-5">Add Item Form</h3>
            <form [formGroup]="accountForm" autocomplete="on" novalidate (ngSubmit)="additemUser(accountForm.value)" enctype="multipart/form-data">
              <div class="row">
                <div class="col-md-12 mb-4 pb-2">
                  <div class="form-outline">
                    <input type="text" id="ItemCode" formControlName="ItemCode" class="form-control form-control-lg" />
                    <label class="form-label" for="UserName">Item Code</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-6 mb-4">
                  <div class="form-outline">
                    <input type="text" id="ItemName" formControlName="ItemName" class="form-control form-control-lg" />
                    <label class="form-label" for="FirstName">Item Name</label>
                  </div>
                </div>
                <div class="col-md-6 mb-4">
                  <div class="form-outline">
                    <input type="text" id="ItemBrand" formControlName="ItemBrand" class="form-control form-control-lg" />
                    <label class="form-label" for="Nickname">Item Brand</label>
                  </div>
                </div>
                <div class="col-md-6 mb-4">
                  <div class="form-outline">
                    <input type="text" id="UnitPrice" formControlName="UnitPrice" class="form-control form-control-lg" />
                    <label class="form-label" for="LastName">Unit Price:</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-12 mb-4">
                  <button type="submit" class="btn btn-primary btn-lg">ADD</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
