import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { CustomerInterface } from '../../interfaces/CustomerInterface';

@Component({
  selector: 'register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  title = 'Registration [CUSTOMER]';

  accountForm: FormGroup;

  constructor(private router: Router, private http: HttpClient) {
    this.accountForm = new FormGroup({
      UserName: new FormControl('', [Validators.required, Validators.minLength(4)]),
      FirstName: new FormControl('', [Validators.required, Validators.minLength(3)]),
      LastName: new FormControl('', [Validators.required, Validators.minLength(3)]),
      Nickname: new FormControl('', [Validators.required, Validators.minLength(3)]),
      EmailAddress: new FormControl('', [Validators.required, Validators.email]),
      Password: new FormControl('', [Validators.required, Validators.pattern(/(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*#?&^_-]).{8,}/)])
    });
  }

  ngOnInit(): void {

  }

  public validateControl = (controlName: string) => {
    return this.accountForm.get(controlName)!.invalid && this.accountForm.get(controlName)!.touched;
  }

  public registerUser = (registerFormValue: any) => {
    const formValues = { ...registerFormValue };
    const customer: CustomerInterface = {
      Id: 0,
      UserName: formValues.UserName,
      FirstName: formValues.FirstName,
      LastName: formValues.LastName,
      Nickname: formValues.Nickname,
      EmailAddress: formValues.EmailAddress,
      Password: formValues.Password
    };

    this.http.post('http://localhost:4200/Register', customer).subscribe(
      response => {
      }
    );
  };
}
<section class="vh-90 gradient-custom">
  <div class="container py-2 h-90">
    <div class="row justify-content-center align-items-center h-100">
      <div class="col-12 col-lg-9 col-xl-7">
        <div class="card shadow-2-strong card-registration forms" style="border-radius: 20px;">
          <div class="card-body p-3 p-md-4">
            <h3 class="mb-4 pb-2 pb-md-0 mb-md-5">Registration Form</h3>
            <form [formGroup]="accountForm" autocomplete="on" novalidate (ngSubmit)="registerUser(accountForm.value)" enctype="multipart/form-data">
              <div class="row">
                <div class="col-md-12 mb-4 pb-2">
                  <div class="form-outline">
                    <input type="text" id="UserName" formControlName="UserName" class="form-control form-control-lg" placeholder="UserName123" />
                    <label class="form-label" for="UserName">Username</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-6 mb-4">
                  <div class="form-outline">
                    <input type="text" id="FirstName" formControlName="FirstName" class="form-control form-control-lg" placeholder="Unknown" />
                    <label class="form-label" for="FirstName">First Name</label>
                  </div>
                </div>
                <div class="col-md-6 mb-4">
                  <div class="form-outline">
                    <input type="text" id="Nickname" formControlName="Nickname" class="form-control form-control-lg" placeholder="Nickname" />
                    <label class="form-label" for="Nickname">Nickname</label>
                  </div>
                </div>
                <div class="col-md-6 mb-4">
                  <div class="form-outline">
                    <input type="text" id="LastName" formControlName="LastName" class="form-control form-control-lg" placeholder="User" />
                    <label class="form-label" for="LastName">Last Name</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-12 mb-4">
                  <div class="form-outline">
                    <input type="email" id="EmailAddress" formControlName="EmailAddress" class="form-control form-control-lg" placeholder="user@example.com" />
                    <label class="form-label" for="EmailAddress">Email</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-6 mb-4 pb-2">
                  <div class="form-outline">
                    <input type="password" id="Password" formControlName="Password" class="form-control form-control-lg" placeholder="Password" />
                    <label class="form-label" for="Password">Password</label>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-12 mb-4">
                  <button type="submit" class="btn btn-primary btn-lg">Register</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
