import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CustomerService } from '../../services/CustomerService';
import { CustomerInterface } from '../../interfaces/CustomerInterface';

@Component({
  selector: 'register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  title = 'Registration [CUSTOMER]';

  accountForm: FormGroup;
  showSuccess: boolean = false;
  successMessage: string = '';

  constructor(private customerService: CustomerService, private router: Router) {
    this.accountForm = new FormGroup({
      UserName: new FormControl('', [Validators.required, Validators.minLength(4)]),
      FirstName: new FormControl('', [Validators.required, Validators.minLength(3)]),
      LastName: new FormControl('', [Validators.required, Validators.minLength(3)]),
      Nickname: new FormControl('', [Validators.required, Validators.minLength(4)]),
      EmailAddress: new FormControl('', [Validators.required, Validators.email]),
      Password: new FormControl('', [Validators.required, Validators.pattern(/(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@$!%*#?&^_-]).{8,}/)])
    });
  }

  ngOnInit(): void {

  }

  public validateControl = (controlName: string) => {
    return this.accountForm.get(controlName)!.invalid && this.accountForm.get(controlName)!.touched;
  }

  public registerUser = (registerFormValue: any) => {
    this.showSuccess = false;
    const formValues = { ...registerFormValue };
    const user: CustomerInterface = {
      Id: 0,
      UserName: formValues.UserName,
      FirstName: formValues.FirstName,
      LastName: formValues.LastName,
      Nickname: formValues.Nickname,
      EmailAddress: formValues.EmailAddress,
      Password: formValues.Password
    };
  }

}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>{{ title }}</title>
  <link rel="stylesheet" href="register.component.css">
</head>

<body>
  <h1>Registration [CUSTOMER]</h1>

  <form [formGroup]="accountForm" (ngSubmit)="registerUser(accountForm.value)">
    <div>
      <label for="UserName">Username:</label>
      <input type="text" id="UserName" formControlName="UserName">
      <div *ngIf="validateControl('UserName')">Username is required and should have a minimum length of 4.</div>
    </div>

    <div>
      <label for="FirstName">First Name:</label>
      <input type="text" id="FirstName" formControlName="FirstName">
      <div *ngIf="validateControl('FirstName')">First Name is required and should have a minimum length of 3.</div>
    </div>

    <div>
      <label for="LastName">Last Name:</label>
      <input type="text" id="LastName" formControlName="LastName">
      <div *ngIf="validateControl('LastName')">Last Name is required and should have a minimum length of 3.</div>
    </div>

    <div>
      <label for="Nickname">Nickname:</label>
      <input type="text" id="Nickname" formControlName="Nickname">
      <div *ngIf="validateControl('Nickname')">Nickname is required and should have a minimum length of 4.</div>
    </div>

    <div>
      <label for="EmailAddress">Email Address:</label>
      <input type="email" id="EmailAddress" formControlName="EmailAddress">
      <div *ngIf="validateControl('EmailAddress')">Email Address is required and should be a valid email.</div>
    </div>

    <div>
      <label for="Password">Password:</label>
      <input type="password" id="Password" formControlName="Password">
      <div *ngIf="validateControl('Password')">Password is required and should have at least 8 characters including one uppercase letter, one lowercase letter, one digit, and one special character.</div>
    </div>

    <button type="submit">Register</button>
  </form>

</body>
</html>
